mapper.update:
UPDATE post
SET category_id = #{categoryId},
    subject     = #{subject},
    content     = #{content},
    pinned      = #{pinned}
WHERE id = #{id}

mapper.select:
SELECT view_count
FROM post
WHERE id = #{postId};

mapper.update:
UPDATE post
SET view_count = view_count + 1
WHERE id = #{postId}
